  const tax = +(subtotal * 0.16).toFixed(2);
  const shipment = +Number(order.shipment ?? 0).toFixed(2);
  const others = +Number(order.others ?? 0).toFixed(2);
  const total = +(subtotal + tax + shipment + others).toFixed(2);

